import React, { Component } from "react";
import { CssBaseline, Typography,Container } from '@material-ui/core';
import Button from '@mui/material/Button';
import SendIcon from '@mui/icons-material/Send';
import Intro from './Intro'
import Userinfo from './Userinfo'
import Sibal from './Sibal'



class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
          showInfo: 0,
          showResult: false
        }
        
        this._onStartClick = this._onStartClick.bind(this)
        this._onResultClick = this._onResultClick.bind(this)

      }
    
    renderIntro() {
        return <Intro _onStartClick={this._onStartClick} title="" />
        // 여기가 타이틀
      }
      
    renderUserinfo() {
        return <Userinfo _onStartClick={this._onStartClick} />
    }
    renderSibal()
    {
      return <Sibal/>
    }
  

   
    render() {
        let showInfo = this.state.showInfo 
        let showResult = this.state.showResult

        //this.state 를 사용함으로써 위에 props에 있는 state, 즉 여기서는 showquestion의 상태를 불러옴
       
       
        if (showInfo==0 && showResult==false)
          return this.renderIntro()

        else if (showInfo==1)
        {
        return this.renderUserinfo()
        }

        else 
        return this.renderSibal()

        
      }
    
      _onStartClick() {
       this.setState({ showInfo: this.state.showInfo + 1})
       console.log(this.state.showResult);
       console.log(this.state.showInfo);


      }
      _onResultClick() {
        this.setState({ showInfo: !this.state.showResult})
        console.log(this.state.showResult);
       }

    }


export default App
